import React, { Component } from 'react';
import Loader from "react-loader-spinner";


import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImages from './components/imageApi';
import Modal from './components/Modal'
import Button from "./components/Button";

import './App.css';
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";


class App extends Component {

  state = {
    querySearch: '',
    queryResult: [],
    page: 1,
    imageUrl: '',
    modalStatus: false,
    loaderStatus: false,
  }


    ;
  componentDidUpdate(prevProps, prevState) {
    if (prevState.querySearch !== this.state.querySearch) {
      console.log('fetch get page', this.state.page)
      console.log('fetch get query', this.state.querySearch)
      this.handleFetchApi(this.state.querySearch)
      this.setState({ button: window.document.querySelector(".Button") })
      this.scrollToBottom()
    }

  }

  scrollToBottom = () => {
    let height = document.documentElement.clientWidth;
    window.scrollBy(0, height, "smooth")
  }

  handleToggleModal = () => {
    this.setState(({ modalStatus }) => ({
      modalStatus: !modalStatus
    }))
  }

  handleFetchApi = (query) => {
    fetchImages(query, this.state.page)
      .then((res) => {
        this.setState((prevState) => ({ queryResult: [...prevState.queryResult, ...res.data.hits] }))
      })
      .catch(error => console.log("error", error))
    this.setState(prevState => ({ page: prevState.page + 1 }));
    this.setState({ loaderStatus: false })

  }

  handleQueruSearch = (query) => {
    this.setState(({
      loaderStatus: true,
      querySearch: query,
      page: 1,
      queryResult: []
    }))
  }

  handleOnClickImage = (url) => {
    this.setState({
      imageUrl: url,
    })
    this.handleToggleModal()
  }

  hangleLoadMore = () => {
    console.log('hello', this.state.querySearch, this.state.page)
    this.handleFetchApi(this.state.querySearch)

  }

  render() {
    const { queryResult, imageUrl, modalStatus, loaderStatus, messagesEndRef } = this.state;

    return (
      <div className="Container">
        <Searchbar onClick={this.handleQueruSearch} />
        {queryResult.length > 0 && < ImageGallery images={queryResult} onImageClick={this.handleOnClickImage}
          loaderStatus={loaderStatus} />}
        {queryResult.length > 11 && <Button onButtonClick={this.hangleLoadMore} className="Button" ref={messagesEndRef} />}


        {!!modalStatus &&
          <Modal handleToggleModal={this.handleToggleModal}>
            <img src={imageUrl} alt="some pic" />
          </Modal>}
      </div>
    );
  }
}

export default App;
