import { Component } from 'react';
import Loader from "react-loader-spinner";


import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImages from './components/imageApi';
import Modal from './components/Modal'
import Button from "./components/Button";

import './App.css';
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";


class App extends Component {

  state = {
    querySearch: '',
    queryResult: [],
    page: 1,
    imageUrl: '',
    modalStatus: false,
    loaderStatus: false,
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.querySearch !== this.state.querySearch) {
      console.log('fetch get page', this.state.page)
      this.handleFetchApi(this.state.querySearch)
    }

    // if (prevState.queryResult !== this.state.queryResult) {
    //   this.handleFetchApi(this.state.querySearch)
    // }

  }

  handleToggleModal = () => {
    this.setState(({ modalStatus }) => ({
      modalStatus: !modalStatus
    }))
  }

  handleFetchApi = (query) => {
    fetchImages(query, this.state.page)
      .then((res) => {
        this.setState((prevState) => ({ queryResult: [...prevState.queryResult, ...res.data.hits] }))
      })
      .catch(error => console.log("error", error))
    this.setState(prevState => ({ page: prevState.page + 1 }));
  }

  handleQueruSearch = (query) => {
    this.setState(prevState => ({
      loaderStatus: true,
      querySearch: query,
      page: 1,
    }))
  }

  handleOnClickImage = (url) => {
    this.setState({
      imageUrl: url,
    })
    this.handleToggleModal()
  }

  hangleLoadMore = () => {
    console.log('hello', this.state.querySearch, this.state.page)
    // this.setState(prevState => ({queryResult: [...prevState.queryResult, ]}))
    // this.handleFetchApi(this.state.querySearch)
  }

  render() {
    const { queryResult, imageUrl, modalStatus, loaderStatus } = this.state;

    return (
      <div className="Container">
        <Searchbar onClick={this.handleQueruSearch} />
        {loaderStatus && <Loader type="Rings" color=" #3f51b5" height={70} width={70} />}
        {queryResult.length > 0 && < ImageGallery images={queryResult} onImageClick={this.handleOnClickImage}
          onButtonClick={this.hangleLoadMore} />}
        {!!modalStatus &&
          <Modal handleToggleModal={this.handleToggleModal}>
            <img src={imageUrl} alt="some pic" />
          </Modal>}
      </div>
    );
  }
}

export default App;
