import { Component } from 'react';
import axios from 'axios'

import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';

import './App.css';


class App extends Component {

  state = {
    newvalue: '',
    page: 1,
    queryResult: {},
  }

  componentDidUpdate() {
    console.log("now I am here")
    console.log(this.state.newvalue)
    axios.get(`https://pixabay.com/api?q=${this.state.newvalue}&page=${this.state.page}&key=19685928-f191ac91b9da632ea9a4b744f&image_type=photo&orientation=horizontal&per_page=12`)
      .then(data => console.log.data)
    //fetchImage(this.state.newvalue, this.state.page).then(data => (console.log(data.hits)))
  }

  handleLoadIgames = (queryValue) => {
    this.setState({ newvalue: queryValue });
  }

  render() {
    const { newvalue } = this.state
    console.log('newvalue', newvalue)

    return (
      <div >
        <Searchbar onClick={this.handleLoadIgames} />
        {/* {queryResult && <ImageGallery images={queryResult} />} */}
      </div>
    );
  }
}

export default App;
