import { Component } from 'react';
import axios from 'axios'

import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';
import Modal from './components/Modal'

import './App.css';


class App extends Component {

  state = {
    newvalue: '',
    page: 1,
    queryResult: {},
    imageUrl: [],
    modalState: false,
  }

  // shouldComponentUpdate() {
  //   if (!!this.state.modalState) {
  //     window.addEventListener('keydown', (event) => {
  //       if (event.code === "Escape") {
  //         this.handleToggleModal()
  //       }
  //     })
  //   }
  // }

  componentDidMount() {

  }


  openModalWindow = () => {
    this.handleToggleModal()
    console.log(this.state.modalState)
    window.addEventListener('keydown', (event) => {
      if (event.code === "Escape" && !!this.state.modalState) {
        this.handleToggleModal()
      }
    })


  }




  handleToggleModal = () => {
    this.setState(prevState => ({
      modalState: !prevState.modalState
    }))
  }

  handleLoadIgames = (queryValue) => {
    fetchImage(queryValue, this.state.page)
      .then(res => this.setState({ queryResult: res.data.hits }))
  }

  handleGetImageUrl = (url) => {
    this.setState({
      imageUrl: url
    })
    this.openModalWindow()
  }

  render() {
    const { queryResult, imageUrl, modalState } = this.state;
    console.log(imageUrl)

    return (
      <div >
        <Searchbar onClick={this.handleLoadIgames} />
        {queryResult.length > 0 && <ImageGallery images={queryResult} heandleClick={this.handleGetImageUrl} />}
        {!!modalState && <Modal imageUrl={imageUrl} />}
      </div>
    );
  }
}

export default App;
