import axios from "axios";
import PropTypes from 'prop-types';

const BASE_URL = "https://pixabay.com/api";
const API_KEY = '19685928-f191ac91b9da632ea9a4b744f';

axios.defaults.baseURL = BASE_URL;

const fetchImages = ({ queryResul, page = 1 }) => {
    return axios
        .get(`?q=${queryResul}&page=${page}&key=${API_KEY}&image_type=photo&orientation=horizontal&per_page=12`)
        .then(res => res.data.hits)
}

fetchImages.propTypes = {
    queryResul: PropTypes.string.isRequired,
    page: PropTypes.number
};

export default fetchImages;
