import { Component } from 'react';
import axios from 'axios'

import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';

import './App.css';


class App extends Component {

  state = {
    newvalue: '',
    page: 1,
    queryResult: {},
    imageUrl: ''
  }

  componentDidUpdate() {
  }

  handleLoadIgames = (queryValue) => {
    //  this.setState({ newvalue: queryValue });
    fetchImage(queryValue, this.state.page)
      //.then(res => console.log(res.data.hits))
      .then(res => this.setState({ queryResult: res.data.hits }))
    console.log('queryResult', this.state.queryResult)
  }

  handleGetImageUrl = (url) => {
    this.setState({ imageUrl: url })
    console.log(this.state.imageUrl)
  }

  render() {
    const { queryResult } = this.state
    console.log('newvalue', queryResult)

    return (
      <div >
        <Searchbar onClick={this.handleLoadIgames} />
        {queryResult.length > 0 && <ImageGallery images={queryResult} onClick={this.handleGetImageUrl} />}
      </div>
    );
  }
}

export default App;
