import React, { Component } from 'react'
import axios from 'axios'

import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';

import './App.css';


class App extends Component {

  state = {
    text: '',
    page: 1,
    queryResult: {},
  }

  componentDidUpdate() {

    fetchImage(this.state.text, this.state.page).then(res => this.setState({ queryResult: res.data.hits }))
    console.log(this.state.queryResult)
  }

  handleSearchText = (ev) => {
    const inputValue = ev.target.value;
    if (inputValue) {
      this.setState({ text: inputValue })
    }
    console.log('text', this.state.text)

  }

  render() {
    const { text, queryResult } = this.props
    return (
      <div >
        <Searchbar onSubmit={this.handleSearchText} />
        {queryResult && <ImageGallery images={queryResult} />}
      </div>
    );
  }
}

export default App;
