import { Component } from 'react';
import axios from 'axios'

import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';

import './App.css';


class App extends Component {

  state = {
    newvalue: '',
    page: 1,
    queryResult: {},
  }

  componentDidUpdate() {
    const value = this.state.newvalue;
    const page = this.state.page;
    console.log('set', value)
    fetchImage(value, page).then(res => this.setState({ queryResult: res.data.hits }))
    console.log("queryResult", this.state.queryResult)
  }

  handleLoadIgames = (text) => {
    //console.log('get', text);
    this.setState({ newvalue: text });
    // console.log('newQuery before update', this.state.newQuery)
    // console.log('newQuery before after', this.state.newQuery)
    // console.log('state', this.state)
    //console.log(this.state.newvalue)
  }

  render() {
    const { newvalue } = this.state
    console.log('newvalut', newvalue)

    return (
      <div >
        <Searchbar onClick={this.handleLoadIgames} />
        {/* {queryResult && <ImageGallery images={queryResult} />} */}
      </div>
    );
  }
}

export default App;
