import { Component } from 'react';

import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';
import Modal from './components/Modal'

import './App.css';


class App extends Component {

  state = {
    page: 1,
    queryResult: {},
    imageUrl: [],
    modalStatus
      : false,
  }

  componentDidMount() {
    window.addEventListener('keydown', (event) => {
      if (event.code === "Escape" && !!this.state.modalStatus) {
        this.handleToggleModal()
      }
    })
  }

  handleToggleModal = () => {
    this.setState(prevState => ({
      modalState: !prevState.modalStatus

    }))
  }

  handleLoadIgames = (queryValue) => {
    fetchImage(queryValue, this.state.page)
      .then(res => this.setState({ queryResult: res.data.hits }))
  }

  handleOnClickImage = (url) => {
    this.setState({
      imageUrl: url
    })
    this.handleToggleModal()
  }

  render() {
    const { queryResult, imageUrl, modalStatus
    } = this.state;
    console.log(imageUrl)

    return (
      <div >
        <Searchbar onClick={this.handleLoadIgames} />
        {queryResult.length > 0 && <ImageGallery images={queryResult} heandleClick={this.handleOnClickImage} />}
        {!!modalStatus
          && <Modal imageUrl={imageUrl} />}
      </div>
    );
  }
}

export default App;
