import { Component } from 'react';
import axios from 'axios'

import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';
import Modal from './components/Modal'
import Button from "./components/Button";

import './App.css';


class App extends Component {

  state = {
    queryValue: '',
    queryResult: {},
    page: 1,
    imageUrl: '',
    modalStatus: false,
  }

  componentDidMount() {
    window.addEventListener('keydown', (event) => {
      if (event.code === "Escape" && !!this.state.modalStatus) {
        console.log('hello')
        this.handleToggleModal()
      }
    })
  }

  componentDidUpdate() {
    fetchImage(this.state.queryValue, this.state.page)
      .then(res => this.setState({ queryResult: res.data.hits }))
  }

  handleToggleModal = () => {
    this.setState(prevState => ({
      modalStatus: !prevState.modalStatus
    }))
  }

  handleQueruValue = (queryValue) => {
    this.setState({ queryValue: queryValue })
  }

  handleOnClickImage = (url) => {
    this.setState({
      imageUrl: url
    })
    this.handleToggleModal()
  }

  render() {
    const { queryResult, imageUrl, modalStatus } = this.state;
    console.log(imageUrl)

    return (
      <div >
        <Searchbar onClick={this.handleQueruValue} />
        {queryResult.length > 0 && <ImageGallery images={queryResult} heandleClick={this.handleOnClickImage} />}
        {queryResult.length > 0 && <Button />}
        {!!modalStatus && <Modal imageUrl={imageUrl} />}
      </div>
    );
  }
}

export default App;
