import { Component } from 'react';
import Loader from "react-loader-spinner";


import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';
import Modal from './components/Modal'
import Button from "./components/Button";

import './App.css';
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";


class App extends Component {

  state = {
    query: '',
    queryResult: {},
    page: 1,
    imageUrl: '',
    modalStatus: false,
    loaderStatus: false,
  }

  componentDidUpdate(prevState, newState) {
    if (prevState.query !== newState.query) {
      this.handleFetchApi(this.state.query);
      this.setState({ loaderStatus: false })
    }
  }

  handleToggleModal = () => {
    this.setState(({ modalStatus }) => ({
      modalStatus: !modalStatus
    }))
  }

  handleFetchApi = (queryValue) => {
    fetchImage(queryValue, this.state.page)
      .then((res) => {
        this.setState({ queryResult: res.data.hits })
      })
      .catch(error => console.log("error", error))
  }

  handleQueruSearch = (query) => {
    this.setState({
      loaderStatus: true,
      query: query,
    })
  }

  handleOnClickImage = (url) => {
    this.setState({
      imageUrl: url,
    })
    this.handleToggleModal()
  }

  hangleLoadMore = () => {

  }

  render() {
    const { queryResult, imageUrl, modalStatus, loaderStatus } = this.state;

    return (
      <div className="Container">
        <Searchbar onClick={this.handleQueruSearch} />
        {loaderStatus && <Loader
          type="Rings"
          color=" #3f51b5"
          height={70}
          width={70}
        />}
        {queryResult.length > 0 && < ImageGallery images={queryResult} handleClick={this.handleOnClickImage} />}
        {queryResult.length > 0 && <Button onClick={this.hangleLoadMore} />}
        {!!modalStatus &&
          <Modal handleToggleModal={this.handleToggleModal}>
            <img src={imageUrl} alt="some pic" />
          </Modal>}
      </div>
    );
  }
}

export default App;
