import React, { Component } from 'react'
import axios from 'axios'

import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';

import './App.css';


class App extends Component {

  state = {
    page: 1,
    queryResult: {},
  }

  componentDidUpdate() {
    fetchImage(this.state.text, this.state.page).then(res => console.log('result was update', res))
    // console.log("queryResult", this.state.queryResult)
  }

  handleLoadIgames = (queryValue) => {
    // fetchImage(this.state.text, this.state.page).then(res => this.setState({ queryResult: res.data.hits }))
    queryValue && this.setState({ queryResult: queryValue })
    console.log("queryresult", this.state.queryResult)
  }

  render() {
    const { text, queryResult } = this.props
    return (
      <div >
        <Searchbar onClick={this.handleLoadIgames} />
        {queryResult && <ImageGallery images={queryResult} />}
      </div>
    );
  }
}

export default App;
