import React, { Component } from 'react'

import Searchbar from './components/Searchbar';

import './App.css';





class App extends Component {

  state = {
    text: ''
  }

  handleSearchText = (ev) => {
    const inputValue = ev.targen.value;
    if (inputValue) {
      this.setState({ text: inputValue })
    }

  }

  render() {
    const { text } = this.props
    return (
      <div >
        <Searchbar text={text} onSubmit={this.handleSearchText} />
      </div>
    );
  }
}

export default App;
