import { Component } from 'react';
import Loader from "react-loader-spinner";


import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';
import Modal from './components/Modal'
import Button from "./components/Button";

import './App.css';
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";



class App extends Component {

  state = {
    queryValue: '',
    queryResult: {},
    page: 1,
    imageUrl: '',
    modalStatus: false,
    loaderStatus: true,
  }

  componentDidMount() {
    this.handleEscapeClick();

  }

  componentDidUpdate() {
    this.handleFetchQuery();
  }

  handleEscapeClick = () => {
    window.addEventListener('keydown', (event) => {
      if (this.state.modalStatus && event.code === "Escape") {
        this.handleToggleModal()
      }
    })
  }

  handleFetchQuery = () => {
    if (this.state.queryValue !== '') {
      fetchImage(this.state.queryValue, this.state.page)
        .then(res => this.setState({ queryResult: res.data.hits }))
        .catch(error => console.log("error", error)).finally(this.setState({ queryResult: {} }))
    }
  }

  handleToggleModal = () => {
    this.setState(prevState => ({
      modalStatus: !prevState.modalStatus
    }))
  }

  handleQueruValue = (queryValue) => {
    this.setState({ queryValue: queryValue })
  }

  handleOnClickImage = (url) => {
    this.setState({
      imageUrl: url
    })
    this.handleToggleModal()
  }

  render() {
    const { queryResult, imageUrl, modalStatus, loaderStatus } = this.state;

    return (
      <div >
        <Searchbar onClick={this.handleQueruValue} />
        {loaderStatus && <Loader />}
        {queryResult.length > 0 && < ImageGallery images={queryResult} heandleClick={this.handleOnClickImage} />}
        {queryResult.length > 0 && <Button />}
        {!!modalStatus && <Modal imageUrl={imageUrl} />}
      </div>
    );
  }
}

export default App;
