import { Component } from 'react';
import Loader from "react-loader-spinner";


import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImage from './components/imageApi';
import Modal from './components/Modal'
import Button from "./components/Button";

import './App.css';
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";

const INITIAL_STATE =
{
  queryValue: '',
  queryResult: {},
  page: 1,
  imageUrl: '',
  modalStatus: false,
  loaderStatus: false,
}


class App extends Component {

  state = {
    queryValue: '',
    queryResult: {},
    page: 1,
    imageUrl: '',
    modalStatus: false,
    loaderStatus: false,
  }

  componentDidUpdate() {
    // this.handleFetchQuery();

  }

  handleFetchQuery = () => {

  }

  handleToggleModal = () => {
    this.setState(({ modalStatus }) => ({
      modalStatus: !modalStatus
    }))
  }

  handleQueruSearch = (queryValue) => {
    this.setState({ loaderStatus: true })
    if (queryValue) {
      //this.setState({ loaderStatus: true })
      fetchImage(queryValue, this.state.page)
        .then((res) => {
          this.setState({ queryResult: res.data.hits, loaderStatus: false })
        })
        .catch(error => console.log("error", error))
    }
    //this.setState({ loaderStatus: true })
  }

  handleOnClickImage = (url) => {
    this.setState({
      imageUrl: url,
      loaderStatus: true,
    })
    this.handleToggleModal()
  }

  render() {
    const { queryResult, imageUrl, modalStatus, loaderStatus } = this.state;

    return (
      <div >
        <Searchbar onClick={this.handleQueruSearch} />
        {loaderStatus && <Loader
          type="Rings"
          color=" #3f51b5"
          height={70}
          width={70}
        />}
        {queryResult.length > 0 && < ImageGallery images={queryResult} handleClick={this.handleOnClickImage} />}
        {queryResult.length > 0 && <Button />}
        {!!modalStatus &&
          <Modal handleToggleModal={this.handleToggleModal}>
            <img src={imageUrl} alt="some pic" />
          </Modal>}
      </div>
    );
  }
}

export default App;
