import React, { Component } from 'react';
import Loader from "react-loader-spinner";


import Searchbar from './components/Searchbar';
import ImageGallery from './components/ImageGallery';
import fetchImages from './components/imageApi';
import Modal from './components/Modal'
import Button from "./components/Button";

import './App.css';
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";


class App extends Component {

  state = {
    querySearch: '',
    queryResult: [],
    page: 1,
    imageUrl: '',
    modalStatus: false,
    loaderStatus: false,
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.querySearch !== this.state.querySearch) {
      this.handleFetchApi()
    }

    if (this.state.queryResult.length > 12) {
      this.scrollToBottom()
    }
  }

  scrollToBottom = () => {
    let viewPortHeight = document.documentElement.clientWidth;
    window.scrollBy(0, viewPortHeight)
  }

  handleToggleModal = () => {
    this.setState(({ modalStatus }) => ({
      modalStatus: !modalStatus
    }))
  }

  handleFetchApi = () => {
    fetchImages(this.state.querySearch, this.state.page)
      .then((res) => {
        this.setState((prevState) => ({ queryResult: [...prevState.queryResult, ...res.data.hits] }))
      })
      .catch(error => console.log("error", error))
    this.setState(prevState => ({ page: prevState.page + 1 }));
    // this.setState({ loaderStatus: false })
  }

  handleQueruSearch = (query) => {
    this.setState(({
      loaderStatus: true,
      querySearch: query,
      page: 1,
      queryResult: []
    }))
  }

  handleOpenModal = (url) => {
    this.setState({
      imageUrl: url,
    })
    this.handleToggleModal()
  }

  render() {
    console.log("loader", this.state.loaderStatus)
    const { queryResult, imageUrl, modalStatus, loaderStatus } = this.state;

    return (
      <div className="Container">
        <Searchbar onClick={this.handleQueruSearch} />
        {queryResult.length > 0 && < ImageGallery images={queryResult} onImageClick={this.handleOpenModal} />}
        {loaderStatus && <Loader type="Rings" color=" #3f51b5" height={70} width={70} />}
        {queryResult.length > 11 && <Button
          type="button"
          onButtonClick={this.handleFetchApi}
          className="Button" />}
        {!!modalStatus &&
          <Modal handleToggleModal={this.handleToggleModal}>
            <img src={imageUrl} alt="pic" />
          </Modal>}
      </div>
    );
  }
}

export default App;
